const [activeTab, setActiveTab] = React.useState("experience");

<Tabs>
  <TabsList>
    <TabsTrigger onClick={() => setActiveTab("experience")}>Experience</TabsTrigger>
    <TabsTrigger onClick={() => setActiveTab("education")}>Education</TabsTrigger>
    <TabsTrigger onClick={() => setActiveTab("skills")}>Skills</TabsTrigger>
  </TabsList>

  {activeTab === "experience" && <TabsContent>{/* Experience content */}</TabsContent>}
  {activeTab === "education" && <TabsContent>{/* Education content */}</TabsContent>}
  {activeTab === "skills" && <TabsContent>{/* Skills content */}</TabsContent>}
</Tabs>
