const [activeTab, setActiveTab] = React.useState("experience");

<Tabs>
  <TabsList>
    <TabsTrigger onClick={() => setActiveTab("experience")}>Experiencia</TabsTrigger>
    <TabsTrigger onClick={() => setActiveTab("education")}>Educación</TabsTrigger>
    <TabsTrigger onClick={() => setActiveTab("skills")}>Habilidades</TabsTrigger>
  </TabsList>

  {activeTab === "experience" && <TabsContent>{/* Contenido de experiencia */}</TabsContent>}
  {activeTab === "education" && <TabsContent>{/* Contenido de educación */}</TabsContent>}
  {activeTab === "skills" && <TabsContent>{/* Contenido de habilidades */}</TabsContent>}
</Tabs>

